import React, { Component, PureComponent } from 'react';

interface ChildProps {
  id: number;
  message: string;
}

// Option 1: Using shouldComponentUpdate
class OptimizedChildComponentSCU extends Component<ChildProps> {
  shouldComponentUpdate(nextProps: ChildProps, nextState: {}) {
    // Only re-render if 'id' prop changes, ignore 'message' prop changes
    const shouldUpdate = nextProps.id !== this.props.id;
    console.log(`OptimizedChildComponentSCU (ID: ${this.props.id}, Message: ${this.props.message}): shouldComponentUpdate returned ${shouldUpdate}`);
    return shouldUpdate;
  }

  render() {
    console.log(`OptimizedChildComponentSCU (ID: ${this.props.id}, Message: ${this.props.message}): Rendered`);
    return (
      <div style={{ border: '1px solid lightgreen', padding: '10px', margin: '5px' }}>
        <h4>Optimized Child (using shouldComponentUpdate)</h4>
        <p>ID: {this.props.id}</p>
        <p>Message: {this.props.message}</p>
      </div>
    );
  }
}

// Option 2: Using PureComponent (and explaining its behavior for this specific case)
class OptimizedChildComponentPure extends PureComponent<ChildProps> {
  render() {
    console.log(`OptimizedChildComponentPure (ID: ${this.props.id}, Message: ${this.props.message}): Rendered`);
    return (
      <div style={{ border: '1px solid lightcoral', padding: '10px', margin: '5px' }}>
        <h4>Optimized Child (using PureComponent)</h4>
        <p>ID: {this.props.id}</p>
        <p>Message: {this.props.message}</p>
      </div>
    );
  }
}
/*
Explanation for PureComponent:
PureComponent performs a shallow comparison of ALL props. In this case, if 'message' prop changes, 
PureComponent WILL detect that change (since 'message' is a string, a primitive, it will be referentially different)
and WILL trigger a re-render. Therefore, PureComponent does NOT meet the specific requirement of 
*only* re-rendering when 'id' changes and ignoring 'message'. For that specific control, 
shouldComponentUpdate with custom logic is necessary.
*/

interface ParentState {
  counter: number;
  statusMessage: string;
}

class ParentComponent extends Component<{}, ParentState> {
  state = {
    counter: 0,
    statusMessage: 'Initial Status',
  };

  render() {
    return (
      <div style={{ border: '1px solid darkblue', padding: '10px', margin: '10px' }}>
        <h2>Parent Component</h2>
        <button onClick={() => this.setState(prevState => ({ counter: prevState.counter + 1 }))}>
          Increment Counter (Changes ID Prop)
        </button>
        <button onClick={() => this.setState({ statusMessage: `Status Update ${Math.random().toFixed(2)}` })}>
          Change Status Message (Changes Message Prop)
        </button>
        <p>Parent Counter: {this.state.counter}</p>

        {/* Choose one of the optimized child components */}
        <OptimizedChildComponentSCU id={this.state.counter} message={this.state.statusMessage} />
        {/* <OptimizedChildComponentPure id={this.state.counter} message={this.state.statusMessage} /> */}
      </div>
    );
  }
}

export default ParentComponent;

