      <h4>Display Count Component</h4>
      <p>Count: {count}</p>
      <p>Status: {status}</p>
    </div>
  );
};

// Custom comparison function for React.memo
// Returns true if props are EQUAL (i.e., no re-render needed)
// We want to re-render ONLY if 'count' changes, ignoring 'status'.
const areDisplayCountPropsEqual = (prevProps: DisplayCountProps, nextProps: DisplayCountProps): boolean => {
  return prevProps.count === nextProps.count;
};

const MemoizedDisplayCount = React.memo(DisplayCount, areDisplayCountPropsEqual);

const ParentCounter: React.FC = () => {
  const [totalCount, setTotalCount] = useState(0);
  const [message, setMessage] = useState('Idle');

  return (
    <div style={{ border: '1px solid darkblue', padding: '10px', margin: '10px' }}>
      <h2>Parent Counter</h2>
      <button onClick={() => setTotalCount(prev => prev + 1)}>
