      <button onClick={() => setMessage(`Message ${Math.random().toFixed(2)}`)}>
        Change Message (Changes 'status' prop)
      </button>
      <p>Total Count: {totalCount}</p>
      <MemoizedDisplayCount count={totalCount} status={message} />
    </div>
  );
};

export default ParentCounter;

