import React, { useState } from 'react';

interface User {
  id: number;
  name: string;
  email: string;
  lastActivity: Date;
}

interface UserCardProps {
  user: User;
}

const UserCard: React.FC<UserCardProps> = ({ user }) => {
  console.log(`UserCard (User ID: ${user.id}, Name: ${user.name}): Rendered`);
  return (
    <div style={{ border: '1px solid orange', padding: '15px', margin: '10px' }}>
      <h3>User Card</h3>
      <p>ID: {user.id}</p>
      <p>Name: {user.name}</p>
      <p>Email: {user.email}</p>
      <p>Last Activity: {user.lastActivity.toLocaleTimeString()}</p>
    </div>
  );
};

// Custom comparison function for React.memo
// Returns true if props are EQUAL (i.e., no re-render needed)
// We want to re-render ONLY if id, name, or email changes, ignoring lastActivity.
const areUserCardPropsEqual = (prevProps: UserCardProps, nextProps: UserCardProps): boolean => {
  return (
    prevProps.user.id === nextProps.user.id &&
    prevProps.user.name === nextProps.user.name &&
    prevProps.user.email === nextProps.user.email
    // We explicitly IGNORE prevProps.user.lastActivity === nextProps.user.lastActivity
    // This means changes to lastActivity won't trigger a re-render.
  );
};

const MemoizedUserCard = React.memo(UserCard, areUserCardPropsEqual);

const ParentUserDashboard: React.FC = () => {
  const [currentUser, setCurrentUser] = useState<User>({
    id: 1,
    name: 'Alice',
    email: 'alice@example.com',
    lastActivity: new Date(),
  });

  const updateLastActivity = () => {
    setCurrentUser(prevUser => ({
      ...prevUser,
      lastActivity: new Date(), // New Date object, new 'user' object reference
    }));
