  increment() {
    this.setState(prevState => ({ count: prevState.count + 1 }));
  }

  render() {
    return (
      <div style={{ border: '1px solid blue', padding: '10px' }}>
        <h3>Class Counter: {this.state.count}</h3>
        <button onClick={this.increment}>Increment Manually</button>
      </div>
    );
  }
}
