import React, { useState, useEffect } from 'react';

function FunctionalCounter() {
  const [count, setCount] = useState(0);

  // Effect for auto-incrementing interval (componentDidMount + componentWillUnmount)
  useEffect(() => {
    console.log('FunctionalCounter: Setting up auto-increment interval (mount)');
    const intervalId = window.setInterval(() => {
      setCount(prev => prev + 1);
    }, 1000);

    // Cleanup function: clears interval on unmount
    return () => {
      console.log('FunctionalCounter: Clearing interval (unmount)');
      clearInterval(intervalId);
    };
  }, []); // Empty dependency array: runs once on mount, cleanup on unmount

  // Effect for logging count updates (componentDidUpdate for 'count' state)
  useEffect(() => {
    // Only log if count has changed from its initial value (after initial mount)
    if (count > 0) { 
        console.log(`FunctionalCounter: Count updated to ${count}`);
    }
  }, [count]); // Dependency array: re-run effect when 'count' changes

  const incrementManually = () => {
    setCount(prev => prev + 1);
  };

  return (
    <div style={{ border: '1px solid green', padding: '10px' }}>
      <h3>Functional Counter: {count}</h3>
      <button onClick={incrementManually}>Increment Manually</button>
    </div>
  );
}

export default FunctionalCounter;

