tasks.solutionCode:
import React, { useState, useEffect } from 'react';

interface UserData {
  id: number;
  name: string;
  email: string;
}

interface FunctionalUserFetcherProps {
  userId: number;
}

const FunctionalUserFetcher: React.FC<FunctionalUserFetcherProps> = ({ userId }) => {
  const [user, setUser] = useState<UserData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    console.log(`FunctionalUserFetcher: Fetching user ${userId}...`);
    const fetchUser = async () => {
      setLoading(true);
      setError(null); // Clear previous errors
      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/users/${userId}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch user ${userId}: ${response.statusText}`);
        }
        const data: UserData = await response.json();
        setUser(data);
      } catch (err: any) {
        console.error('Error in fetch:', err);
        setError(err.message || 'An unknown error occurred');
        setUser(null); // Clear user data on error
      } finally {
        setLoading(false);
      }
    };

    fetchUser();

    // Optional: Cleanup function if fetch was cancellable (e.g., AbortController)
    return () => {
        // console.log(`Cleanup for user ${userId} fetch`);
        // if (controller) controller.abort();
    };
  }, [userId]); // Dependency array: re-run effect when userId changes

  if (loading) return <div>Loading user {userId}...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!user) return <div>No user data for ID: {userId}.</div>;

  return (
    <div style={{ border: '1px solid orange', padding: '15px' }}>
      <h3>User Details (Functional Component)</h3>
      <p>ID: {user.id}</p>
      <p>Name: {user.name}</p>
      <p>Email: {user.email}</p>
    </div>
  );
};

export default FunctionalUserFetcher;

