class LegacyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: null };
    console.log('1. Constructor: Initializing state');
  }

  // UNSAFE_componentWillMount is called before mounting occurs
  // Avoid using this method in modern React applications.
  UNSAFE_componentWillMount() {
    console.log('2. UNSAFE_componentWillMount: Called before component mounts (avoid)');
    // Example of what NOT to do: calling setState here will not trigger a re-render,
    // but will update state before initial render. This can lead to issues.
    // this.setState({ data: 'initial data set in cWM' });
  }

  // componentDidMount is ideal for side effects like data fetching
  componentDidMount() {
    console.log('3. componentDidMount: Component mounted, suitable for data fetching');
    // Example: fetch('/api/data').then(res => this.setState({ data: res.data }));
  }

  // UNSAFE_componentWillReceiveProps is called when props update
  // Avoid using this method in modern React applications.
  UNSAFE_componentWillReceiveProps(nextProps) {
    console.log('4. UNSAFE_componentWillReceiveProps: Called when props update (avoid)');
    // Example of what NOT to do: deriving state from props directly here often leads to bugs.
    // if (nextProps.userId !== this.props.userId) {
    //   this.setState({ data: null, loading: true });
    // }
  }

  // getDerivedStateFromProps is the recommended way to derive state from props
  static getDerivedStateFromProps(nextProps, prevState) {
    console.log('getDerivedStateFromProps: Deriving state from props');
    // Example: if (nextProps.userId !== prevState.userId) { return { data: null, userId: nextProps.userId }; } return null;
    return null;
  }

  // shouldComponentUpdate can optimize performance by preventing unnecessary re-renders
  shouldComponentUpdate(nextProps, nextState) {
    console.log('shouldComponentUpdate: Deciding whether to re-render');
    return true; // Always re-render for this example
  }

  // UNSAFE_componentWillUpdate is called before rendering with new props/state
  // Avoid using this method in modern React applications.
  UNSAFE_componentWillUpdate(nextProps, nextState) {
    console.log('5. UNSAFE_componentWillUpdate: Called before re-render (avoid)');
    // Example of what NOT to do: side effects here can cause issues.
  }

  // getSnapshotBeforeUpdate is useful for capturing DOM state before an update
  getSnapshotBeforeUpdate(prevProps, prevState) {
    console.log('getSnapshotBeforeUpdate: Capturing DOM snapshot');
    return null;
  }

  // componentDidUpdate is ideal for side effects after updates
  componentDidUpdate(prevProps, prevState, snapshot) {
    console.log('6. componentDidUpdate: Component updated, suitable for side effects');
  }

  // componentWillUnmount is for cleanup before component unmounts
  componentWillUnmount() {
    console.log('7. componentWillUnmount: Component is about to unmount');
  }

  render() {
    console.log('8. Render: Rendering component UI');
    return (
      <div>
        <h3>Legacy Component Example</h3>
        <p>Data: {this.state.data || 'N/A'}</p>
        <p>Props: {JSON.stringify(this.props)}</p>
      </div>
    );
  }
}

export default LegacyComponent;

