import React, { useState, useMemo } from 'react';

function CartItem({ item, discountPercentage }) {
  const [quantity, setQuantity] = useState(item.quantity);

  // Use useMemo to memoize the totalPrice calculation
  const totalPrice = useMemo(() => {
    console.log(`Calculating total price for ${item.name}...`);
    return item.price * quantity * (1 - discountPercentage);
  }, [item.price, quantity, discountPercentage]); // Dependencies

  const increaseQuantity = () => {
    setQuantity(prevQuantity => prevQuantity + 1);
  };

  const decreaseQuantity = () => {
    setQuantity(prevQuantity => Math.max(1, prevQuantity - 1)); // Ensure quantity doesn't go below 1
  };

  return (
    <div style={{ border: '1px solid #ccc', padding: '15px', margin: '10px', borderRadius: '5px' }}>
      <h3>{item.name}</h3>
      <p>Price per unit: ${item.price.toFixed(2)}</p>
      <p>Quantity:
        <button onClick={decreaseQuantity} disabled={quantity === 1}>-</button>
        {' '}{quantity}{' '}
        <button onClick={increaseQuantity}>+</button>
      </p>
      <p>Discount: {(discountPercentage * 100).toFixed(0)}%</p>
      <p><strong>Total: ${totalPrice.toFixed(2)}</strong></p>
      <small>Check console for 'Calculating total price...' log to see memoization in action.</small>
    </div>
  );
}

export default CartItem;

