import React, { useState, useEffect } from 'react';

function FunctionalCounter() {
  const [count, setCount] = useState(0);

  // Effect for componentDidMount and componentDidUpdate logic
  useEffect(() => {
    if (count === 0) {
      console.log('Functional: Component mounted, count:', count);
    } else {
      console.log('Functional: Component updated, count:', count);
    }

    // Cleanup function for componentWillUnmount
    return () => {
      console.log('Functional: Component unmounting, count (at unmount):', count);
    };
  }, [count]); // Re-run effect when count changes

  const increment = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrement = () => {
    setCount(prevCount => Math.max(0, prevCount - 1));
  };

  return (
    <div>
      <h2>Functional Counter</h2>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default FunctionalCounter;

