import React, { useState } from 'react';

function AuthStatus() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('guest'); // 'guest', 'user', 'admin'
  const [showAdminPanel, setShowAdminPanel] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
    // Randomly assign user or admin role for demonstration
    const roles = ['user', 'admin'];
    const randomRole = roles[Math.floor(Math.random() * roles.length)];
    setUserRole(randomRole);
    console.log(`Logged in as: ${randomRole}`);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole('guest');
    setShowAdminPanel(false); // Hide admin panel on logout
    console.log('Logged out');
  };

  const toggleAdminPanel = () => {
    setShowAdminPanel(prev => !prev);
  };

  return (
    <div style={{ border: '1px solid lightblue', padding: '20px', margin: '20px' }}>
      <h2>Authentication Status</h2>

      {!isLoggedIn ? (
        // Not logged in
        <>
          <p>Please log in to access features.</p>
          <button onClick={handleLogin}>Login</button>
        </>
      ) : (
        // Logged in
        <>
          <p>Welcome, {userRole.charAt(0).toUpperCase() + userRole.slice(1)}!</p>
          <button onClick={handleLogout}>Logout</button>

          {userRole === 'admin' && (
            // Admin specific controls
            <div style={{ marginTop: '10px' }}>
              <button onClick={toggleAdminPanel}>
                {showAdminPanel ? 'Hide Admin Panel' : 'Show Admin Panel'}
              </button>
              {showAdminPanel && (
                <div style={{ border: '1px dashed grey', padding: '10px', marginTop: '10px' }}>
                  <h3>Admin Dashboard Controls</h3>
                  <p>Manage users, view reports, etc.</p>
                </div>
              )}
            </div>
          )}

          {userRole === 'user' && (
            // Regular user specific message
            <p style={{ marginTop: '10px' }}>User Dashboard</p>
          )}
        </>
      )}
    </div>
  );
}

export default AuthStatus;

