import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import RegistrationForm from './RegistrationForm';

describe('RegistrationForm', () => {
  const mockOnSubmit = jest.fn();

  beforeEach(() => {
    mockOnSubmit.mockClear();
  });

  it('renders with initial empty fields and disabled button', () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    expect(screen.getByLabelText(/username/i)).toHaveValue('');
    expect(screen.getByLabelText(/password/i)).toHaveValue('');
    expect(screen.getByRole('button', { name: /register/i })).toBeDisabled();
    expect(screen.queryByTestId('username-error')).not.toBeInTheDocument();
    expect(screen.queryByTestId('password-error')).not.toBeInTheDocument();
  });

  it('shows error messages for invalid username on blur', async () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    const usernameInput = screen.getByLabelText(/username/i);
    fireEvent.change(usernameInput, { target: { value: 'ab' } }); // Less than 3 chars
    fireEvent.blur(usernameInput);

    await waitFor(() => {
      expect(screen.getByTestId('username-error')).toHaveTextContent('Username must be at least 3 characters.');
    });
    expect(screen.getByRole('button', { name: /register/i })).toBeDisabled();
  });

  it('shows error messages for invalid password on blur', async () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    const passwordInput = screen.getByLabelText(/password/i);
    fireEvent.change(passwordInput, { target: { value: '123' } }); // Less than 6 chars
    fireEvent.blur(passwordInput);

    await waitFor(() => {
      expect(screen.getByTestId('password-error')).toHaveTextContent('Password must be at least 6 characters.');
    });
    expect(screen.getByRole('button', { name: /register/i })).toBeDisabled();
  });

  it('enables button and hides errors with valid inputs', async () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    const usernameInput = screen.getByLabelText(/username/i);
    const passwordInput = screen.getByLabelText(/password/i);

    fireEvent.change(usernameInput, { target: { value: 'validuser' } });
    fireEvent.change(passwordInput, { target: { value: 'validpass' } });

    // Trigger blur to ensure errors are gone if they were previously shown
    fireEvent.blur(usernameInput);
    fireEvent.blur(passwordInput);

    await waitFor(() => {
      expect(screen.getByRole('button', { name: /register/i })).toBeEnabled();
    });
    expect(screen.queryByTestId('username-error')).not.toBeInTheDocument();
    expect(screen.queryByTestId('password-error')).not.toBeInTheDocument();
  });

  it('calls onSubmit with correct data on valid submission', async () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    const usernameInput = screen.getByLabelText(/username/i);
    const passwordInput = screen.getByLabelText(/password/i);
    const registerButton = screen.getByRole('button', { name: /register/i });

    fireEvent.change(usernameInput, { target: { value: 'testuser' } });
    fireEvent.change(passwordInput, { target: { value: 'testpassword' } });

    // Ensure button is enabled before clicking
    await waitFor(() => { expect(registerButton).toBeEnabled(); });

    fireEvent.click(registerButton);

    expect(mockOnSubmit).toHaveBeenCalledTimes(1);
    expect(mockOnSubmit).toHaveBeenCalledWith({ username: 'testuser', password: 'testpassword' });
  });

  it('does not call onSubmit on invalid submission', () => {
    render(<RegistrationForm onSubmit={mockOnSubmit} />);
    const usernameInput = screen.getByLabelText(/username/i);
    const registerButton = screen.getByRole('button', { name: /register/i });

    fireEvent.change(usernameInput, { target: { value: 'sh' } }); // Invalid username
    expect(registerButton).toBeDisabled(); // Should remain disabled
    fireEvent.click(registerButton);

    expect(mockOnSubmit).not.toHaveBeenCalled();
  });
});

