import React, { useState, useEffect } from 'react';

// Simulate API call (e.g., in api.js or in the test file)
const fetchUsers = async (shouldFail = false, emptyResult = false) => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      if (shouldFail) {
        reject(new Error('Failed to fetch users.'));
      } else if (emptyResult) {
        resolve([]);
      } else {
        resolve([{ id: 1, name: 'Alice' }, { id: 2, name: 'Bob' }]);
      }
    }, 100);
  });
};

// UserList.tsx
