};

export default UserList;

// UserList.test.tsx
import { render, screen, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import UserList from './UserList';

// Mock the specific `fetchUsers` function that UserList imports
// We need to mock it in the context of the component under test.
// The best way is to put fetchUsers in a separate file (e.g. api.ts) and mock that file.
// For simplicity within a single file example, we can mock the module directly.

jest.mock('./UserList', () => {
  // Get the actual module so we can mock specific functions within it
  const originalModule = jest.requireActual('./UserList');
  return {
    __esModule: true,
    ...originalModule,
    fetchUsers: jest.fn(), // Mock the fetchUsers function imported by UserList
  };
});

// If fetchUsers was in a separate file like `api.ts`:
// import { fetchUsers } from '../api';
// jest.mock('../api');

describe('UserList', () => {
  // Access the mocked function directly after mocking the module
  const mockFetchUsers = require('./UserList').fetchUsers; // Adjust path if fetchUsers is elsewhere

  beforeEach(() => {
    mockFetchUsers.mockClear();
  });

  it('displays loading message initially', () => {
    mockFetchUsers.mockReturnValueOnce(new Promise(() => {})); // Never resolve to keep loading
    render(<UserList />);
    expect(screen.getByText(/loading users/i)).toBeInTheDocument();
  });

  it('displays users after successful fetch', async () => {
    const users = [{ id: 1, name: 'Alice' }, { id: 2, name: 'Bob' }];
    mockFetchUsers.mockResolvedValueOnce(users);

    render(<UserList />);

    // Wait for loading message to disappear and user names to appear
    await waitFor(() => {
      expect(screen.queryByText(/loading users/i)).not.toBeInTheDocument();
      expect(screen.getByText('Alice')).toBeInTheDocument();
      expect(screen.getByText('Bob')).toBeInTheDocument();
    });
    expect(screen.getByRole('heading', { name: /user list/i })).toBeInTheDocument();
