import React from 'react';

const areEqual = (prevProps, nextProps) => {
  // Return true if passing nextProps to render would return
  // the same result as passing prevProps to render; otherwise return false
  // Here, we only re-render if the length of the data array changes
  return prevProps.data.length === nextProps.data.length;
};

const MemoizedComponentWithCustomComparison = React.memo(function ExpensiveComponent(props) {
  console.log('MemoizedComponentWithCustomComparison rendering...');
  return (
    <div>
      <h4>Custom Memoized Component</h4>
      {props.data.map(item => <div key={item.id}>{item.name}</div>)}
    </div>
  );
}, areEqual);

// Example usage:
function ParentWithCustomMemo() {
  const [value, setValue] = React.useState(0);
  const [items, setItems] = React.useState([
    { id: 1, name: 'Apple' },
    { id: 2, name: 'Banana' }
  ]);

  // This will cause re-renders if the custom comparison only checks length
  // while other properties of an object in the array change
  const updateItemName = () => {
    setItems(prev => prev.map(item => 
      item.id === 1 ? { ...item, name: 'New Apple ' + Math.random() } : item
    ));
  };

  return (
    <div>
      <button onClick={() => setValue(v => v + 1)}>Parent Value: {value}</button>
      <button onClick={updateItemName}>Change Item Name</button>
      <MemoizedComponentWithCustomComparison data={items} />
    </div>
  );
}

