class AppWithImmutablePure extends React.Component {
  state = {
    data: { value: 10, timestamp: Date.now() },
    toggle: false
  };

  updateData = () => {
    // Create a new object for state update to trigger re-render in PureComponent
    this.setState(prevState => ({
      data: { ...prevState.data, value: prevState.data.value + 1, timestamp: Date.now() }
    }));
  };

  toggleState = () => {
    this.setState(prevState => ({ toggle: !prevState.toggle }));
  };

  render() {
    return (
      <div>
        <button onClick={this.updateData}>Update Data</button>
        <button onClick={this.toggleState}>Toggle App State</button>
        <p>App Toggle: {this.state.toggle ? 'On' : 'Off'}</p>
        <OptimizedDataDisplay data={this.state.data} />
      </div>
    );
  }
}

