// Wrapped with React.memo for optimization
const MemoizedExpensiveChild = React.memo(ExpensiveChild);

// --- ParentComponent.tsx ---
export function ParentComponent() {
  const [count, setCount] = React.useState(0);
  const [items, setItems] = React.useState([
    { id: 1, name: 'Item A' },
    { id: 2, name: 'Item B' }
  ]);

  // fixedItems is memoized using useMemo to ensure its reference doesn't change
  // on ParentComponent re-renders, thereby not triggering MemoizedExpensiveChild.
  const fixedItems = React.useMemo(() => [
    { id: 100, name: 'Fixed Item X' },
    { id: 101, name: 'Fixed Item Y' }
  ], []);

  const addNewItem = () => {
    // Creating a new array reference to trigger re-render in MemoizedExpensiveChild
    setItems(prevItems => [...prevItems, { id: prevItems.length + 1, name: `New Item ${prevItems.length + 1}` }]);
  };

  return (
    <div style={{ border: '1px solid blue', padding: '20px' }}>
      <h2>Parent Component</h2>
      <button onClick={() => setCount(count + 1)}>
        Increment Parent Count: {count}
      </button>
      <button onClick={addNewItem}>Add New Item to Child Data</button>

      {/* This instance will only re-render if fixedItems (reference) changes */}
      <MemoizedExpensiveChild data={fixedItems} />
      <hr />
      {/* This instance will re-render if `items` (reference) changes */}
      <MemoizedExpensiveChild data={items} />
    </div>
  );
}

