  email: `user${i + 1}@example.com`,
}));

function UserListFilter() {
  const [searchTerm, setSearchTerm] = useState('');
  const [counter, setCounter] = useState(0); // Unrelated state

  // Use useMemo to memoize the filtered users.
  // This calculation will only re-run if ALL_USERS or searchTerm changes.
  // It will NOT re-run when 'counter' changes.
  const filteredUsers = useMemo(() => {
    console.log('Filtering users...'); // This will log only when needed
    if (!searchTerm) {
      return ALL_USERS;
    }
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    return ALL_USERS.filter(user => 
      user.name.toLowerCase().includes(lowerCaseSearchTerm) ||
      user.email.toLowerCase().includes(lowerCaseSearchTerm)
    );
  }, [ALL_USERS, searchTerm]); // Dependencies: ALL_USERS and searchTerm

  return (
    <div>
      <h1>User List Filter</h1>

      <label htmlFor="search-input">Search Users:</label>
      <input
