import React, { useState, useMemo, useCallback } from 'react';

interface Product {
  id: number;
  name: string;
  price: number;
}

interface ProductListProps {
  products: Product[];
}

function ProductList({ products }: ProductListProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [counter, setCounter] = useState(0); // Unrelated state

  // Use useMemo to memoize the filtered products.
  // This calculation will only re-run if 'products' or 'searchTerm' changes.
  const filteredProducts = useMemo(() => {
    console.log('useMemo: Filtering products...'); // Log to observe re-runs
    if (!searchTerm) {
      return products;
    }
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    return products.filter(product => 
      product.name.toLowerCase().includes(lowerCaseSearchTerm)
    );
  }, [products, searchTerm]); // Dependencies: products and searchTerm

  // Use useCallback to memoize the handleProductClick function.
  // Its reference will remain stable unless its dependencies change.
  // In this case, it doesn't depend on any state/props that change frequently, so an empty array works.
  const handleProductClick = useCallback((id: number) => {
    console.log(`useCallback: Product ${id} clicked!`); // Log to observe function re-creation
  }, []); // Empty dependency array: function created once

  return (
    <div>
      <h1>Product Catalog</h1>
      <input
        type="text"
        placeholder="Search products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <p>Counter: {counter}</p>
      <button onClick={() => setCounter(prev => prev + 1)}>
        Increment Unrelated Counter
      </button>

      <h2>Available Products ({filteredProducts.length})</h2>
      <ul>
        {filteredProducts.map(product => (
          <li key={product.id} onClick={() => handleProductClick(product.id)}>
            {product.name} - ${product.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;

