import React, { useState } from 'react';
import { shallow, mount } from 'enzyme';

interface ToggleSwitchProps {
  label: string;
}

// A simple child component to demonstrate mount vs shallow
function NestedIndicator() {
  return <span className="nested-indicator">[Nested Indicator]</span>;
}

function ToggleSwitch({ label }: ToggleSwitchProps) {
  const [isOn, setIsOn] = useState(false);

  return (
    <button onClick={() => setIsOn(!isOn)} data-testid="toggle-button">
      {label}: {isOn ? 'ON' : 'OFF'}
      <NestedIndicator /> {/* Include the child component */}
    </button>
  );
}

export default ToggleSwitch;

// --- Test File (e.g., ToggleSwitch.test.tsx) ---

describe('ToggleSwitch - Shallow Rendering', () => {
  it('renders correctly with initial OFF state and label', () => {
    const wrapper = shallow(<ToggleSwitch label="Enable Feature" />);
    expect(wrapper.text()).toContain('Enable Feature: OFF');
    // Verify NestedIndicator is present as a component type, not rendered DOM
    expect(wrapper.find('NestedIndicator').exists()).toBe(true);
    expect(wrapper.find('.nested-indicator').exists()).toBe(false); // No actual DOM from child
  });

  it('toggles state to ON on first click', () => {
    const wrapper = shallow(<ToggleSwitch label="Enable Feature" />);
    const button = wrapper.find('[data-testid="toggle-button"]');

    button.simulate('click');
    expect(button.text()).toContain('Enable Feature: ON');
  });

  it('toggles state back to OFF on second click', () => {
    const wrapper = shallow(<ToggleSwitch label="Enable Feature" />);
    const button = wrapper.find('[data-testid="toggle-button"]');

    button.simulate('click'); // ON
    button.simulate('click'); // OFF
    expect(button.text()).toContain('Enable Feature: OFF');
  });
});

describe('ToggleSwitch - Mount Rendering', () => {
  it('renders correctly with initial OFF state and label', () => {
    const wrapper = mount(<ToggleSwitch label="Enable Feature" />);
    expect(wrapper.text()).toContain('Enable Feature: OFF');
    // Verify NestedIndicator's actual DOM is present
    expect(wrapper.find('NestedIndicator').exists()).toBe(true);
    expect(wrapper.find('.nested-indicator').exists()).toBe(true); // Actual DOM from child
  });

  it('toggles state to ON on first click', () => {
    const wrapper = mount(<ToggleSwitch label="Enable Feature" />);
    const button = wrapper.find('[data-testid="toggle-button"]');

    button.simulate('click');
    // Need to `update()` for `mount` to re-render and reflect state changes immediately in the wrapper's DOM
    wrapper.update(); 
    expect(button.text()).toContain('Enable Feature: ON');
  });

  it('toggles state back to OFF on second click', () => {
    const wrapper = mount(<ToggleSwitch label="Enable Feature" />);
    const button = wrapper.find('[data-testid="toggle-button"]');

    button.simulate('click'); // ON
    wrapper.update(); 
    button.simulate('click'); // OFF
    wrapper.update();
    expect(button.text()).toContain('Enable Feature: OFF');
  });
});

