import React from 'react';
import { render, screen } from '@testing-library/react';

const NavBar = ({ adminLinks }) => {
  const defaultLinks = [
    { text: 'Home', path: '/' },
    { text: 'About', path: '/about' },
  ];

  return (
    <nav aria-label="Main Navigation">
      <ul>
        {defaultLinks.map(link => (
          <li key={link.path}><a href={link.path}>{link.text}</a></li>
        ))}
      </ul>
      {adminLinks && adminLinks.length > 0 && (
        <div>
          <h3>Admin</h3>
          <ul>
            {adminLinks.map(link => (
              <li key={link.path}><a href={link.path}>{link.text}</a></li>
            ))}
          </ul>
        </div>
      )}
    </nav>
  );
};

describe('NavBar component', () => {
  it('renders default links correctly', () => {
    render(<NavBar />);
    expect(screen.getByRole('link', { name: 'Home' })).toBeInTheDocument();
    expect(screen.getByRole('link', { name: 'About' })).toBeInTheDocument();
  });

  it('renders admin links and heading when provided', () => {
    const adminLinks = [
      { text: 'Dashboard', path: '/admin/dashboard' },
      { text: 'Users', path: '/admin/users' },
    ];
    render(<NavBar adminLinks={adminLinks} />);

    expect(screen.getByRole('heading', { name: 'Admin' })).toBeInTheDocument();
    expect(screen.getByRole('link', { name: 'Dashboard' })).toBeInTheDocument();
    expect(screen.getByRole('link', { name: 'Users' })).toBeInTheDocument();
  });

  it('does not render admin links or heading when not provided', () => {
    render(<NavBar />);
    expect(screen.queryByRole('heading', { name: 'Admin' })).not.toBeInTheDocument();
    // Using queryByRole/queryByText is important for asserting absence
    expect(screen.queryByRole('link', { name: 'Dashboard' })).not.toBeInTheDocument();
  });

  it('does not render admin links or heading when provided with empty array', () => {
    render(<NavBar adminLinks={[]} />);
    expect(screen.queryByRole('heading', { name: 'Admin' })).not.toBeInTheDocument();
  });
});

