        <input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <button type="submit">Register</button>
    </form>
  );
};

describe('UserRegistrationForm', () => {
  let mockOnSuccess;
  let mockOnError;

  beforeEach(() => {
    mockOnSuccess = jest.fn();
    mockOnError = jest.fn();
    // Clear all mocks before each test to ensure isolation
    global.fetch = jest.fn();
  });

  it('calls onSuccess with data and clears form on successful registration', async () => {
    // Mock a successful fetch response
    global.fetch.mockResolvedValueOnce({
      ok: true,
      json: () => Promise.resolve({ id: 1, username: 'testuser', email: 'test@example.com' }),
    });

    render(
      <UserRegistrationForm onSuccess={mockOnSuccess} onError={mockOnError} />
    );

    // Get input elements by label text
    const usernameInput = screen.getByLabelText(/username:/i);
    const emailInput = screen.getByLabelText(/email:/i);
    const submitButton = screen.getByRole('button', { name: /register/i });

    // Simulate user input
    fireEvent.change(usernameInput, { target: { value: 'testuser' } });
    fireEvent.change(emailInput, { target: { value: 'test@example.com' } });

    // Simulate form submission
    fireEvent.click(submitButton);

    // Wait for the asynchronous operations to complete and state to update
    await waitFor(() => {
      expect(mockOnSuccess).toHaveBeenCalledTimes(1);
      expect(mockOnSuccess).toHaveBeenCalledWith({ id: 1, username: 'testuser', email: 'test@example.com' });
      expect(mockOnError).not.toHaveBeenCalled();
    });

    // Verify form fields are cleared after success
    expect(usernameInput).toHaveValue('');
    expect(emailInput).toHaveValue('');

    // Verify fetch was called with correct arguments
    expect(global.fetch).toHaveBeenCalledTimes(1);
    expect(global.fetch).toHaveBeenCalledWith(
      '/api/register',
      expect.objectContaining({
        method: 'POST',
        body: JSON.stringify({ username: 'testuser', email: 'test@example.com' }),
      })
    );
  });

  it('calls onError and does not clear form on failed registration', async () => {
    // Mock a failed fetch response (e.g., 400 Bad Request)
    global.fetch.mockResolvedValueOnce({
      ok: false,
      status: 400,
      json: () => Promise.resolve({ message: 'Validation failed' }),
    });

    render(
      <UserRegistrationForm onSuccess={mockOnSuccess} onError={mockOnError} />
    );

    const usernameInput = screen.getByLabelText(/username:/i);
    const emailInput = screen.getByLabelText(/email:/i);
    const submitButton = screen.getByRole('button', { name: /register/i });

    fireEvent.change(usernameInput, { target: { value: 'invaliduser' } });
    fireEvent.change(emailInput, { target: { value: 'invalid@example.com' } });

    fireEvent.click(submitButton);

    await waitFor(() => {
      expect(mockOnError).toHaveBeenCalledTimes(1);
      expect(mockOnError).toHaveBeenCalledWith(expect.any(Error)); // Checks it's an Error object
      expect(mockOnSuccess).not.toHaveBeenCalled();
    });

    // Verify form fields are NOT cleared after failure
    expect(usernameInput).toHaveValue('invaliduser');
    expect(emailInput).toHaveValue('invalid@example.com');

    expect(global.fetch).toHaveBeenCalledTimes(1);
  });
});

