  const user = useSelector(state => state.user);
  const dispatch = useDispatch();

  const handleResetName = () => {
    dispatch({ type: RESET_NAME });
  };

  return (
    <div>
      <h2>User Profile</h2>
      <p>First Name: {user.firstName}</p>
      <p>Last Name: {user.lastName}</p>
      <button onClick={handleResetName}>Reset Name</button>
    </div>
  );
};

const mockStore = configureStore([]);

describe('UserProfile component', () => {
  it('renders user information from the Redux store', () => {
    const initialState = {
      user: { firstName: 'John', lastName: 'Doe' }
    };
    const store = mockStore(initialState);

    render(
      <Provider store={store}>
        <UserProfile />
      </Provider>
    );

    expect(screen.getByText(/First Name: John/i)).toBeInTheDocument();
    expect(screen.getByText(/Last Name: Doe/i)).toBeInTheDocument();
  });

  it('dispatches RESET_NAME action when Reset Name button is clicked', () => {
    const initialState = {
      user: { firstName: 'Jane', lastName: 'Smith' }
    };
    const store = mockStore(initialState);

    render(
      <Provider store={store}>
        <UserProfile />
      </Provider>
    );

    const resetButton = screen.getByRole('button', { name: /Reset Name/i });
    fireEvent.click(resetButton);

    // Get all actions that were dispatched to the mock store
    const actions = store.getActions();

    // Assert that the RESET_NAME action was dispatched
    expect(actions).toEqual([{ type: RESET_NAME }]);
    expect(actions).toHaveLength(1); // Ensure only one action was dispatched
  });

  it('renders correctly when user data is missing (edge case)', () => {
    const initialState = { user: {} }; // Missing firstName, lastName
