import { useState, useEffect, useCallback } from 'react';
import { renderHook, act } from '@testing-library/react-hooks';

// Mock localStorage for testing
const localStorageMock = {
  getItem: jest.fn(),
  setItem: jest.fn(),
  removeItem: jest.fn(),
  clear: jest.fn(),
};
Object.defineProperty(window, 'localStorage', {
  value: localStorageMock,
});

const useLocalStorage = (key, initialValue) => {
  const [value, setValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.warn(`Error reading localStorage key ${key}:`, error);
      return initialValue;
    }
  });

  const setStoredValue = useCallback(newValue => {
    try {
      // Allow newValue to be a function, just like useState's setter
      const valueToStore = newValue instanceof Function ? newValue(value) : newValue;
      setValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      console.warn(`Error setting localStorage key ${key}:`, error);
    }
  }, [key, value]); // Add `value` to dependency array for function updates

  // Optionally, you might add a useEffect to listen for storage events
  // For this task, we focus on the setter for updates

  return [value, setStoredValue];
};

describe('useLocalStorage', () => {
  beforeEach(() => {
    localStorageMock.getItem.mockClear();
    localStorageMock.setItem.mockClear();
    localStorageMock.removeItem.mockClear();
    localStorageMock.clear.mockClear();
  });

  it('should initialize with initialValue if localStorage is empty', () => {
    localStorageMock.getItem.mockReturnValue(null);
    const { result } = renderHook(() => useLocalStorage('myKey', 'default'));
    expect(result.current[0]).toBe('default');
    expect(localStorageMock.getItem).toHaveBeenCalledWith('myKey');
  });

  it('should initialize with value from localStorage if present', () => {
    localStorageMock.getItem.mockReturnValue(JSON.stringify('storedValue'));
    const { result } = renderHook(() => useLocalStorage('myKey', 'default'));
    expect(result.current[0]).toBe('storedValue');
    expect(localStorageMock.getItem).toHaveBeenCalledWith('myKey');
  });

  it('should update localStorage when the value changes', () => {
    localStorageMock.getItem.mockReturnValue(null); // Ensure initial state doesn't come from storage
    const { result } = renderHook(() => useLocalStorage('myKey', 'default'));

    act(() => {
      result.current[1]('newValue'); // Call the setter function
    });
    expect(result.current[0]).toBe('newValue');
    expect(localStorageMock.setItem).toHaveBeenCalledWith('myKey', JSON.stringify('newValue'));
    expect(localStorageMock.setItem).toHaveBeenCalledTimes(1); // Only one setItem call from this action
  });

  it('should handle function updates to the value', () => {
    localStorageMock.getItem.mockReturnValue(null);
    const { result } = renderHook(() => useLocalStorage('count', 0));

    act(() => {
      result.current[1](prev => prev + 1);
    });
    expect(result.current[0]).toBe(1);
    expect(localStorageMock.setItem).toHaveBeenCalledWith('count', JSON.stringify(1));
  });

  it('should handle complex objects correctly', () => {
    const initialUser = { name: 'Bob', age: 30 };
    localStorageMock.getItem.mockReturnValue(JSON.stringify(initialUser));
    const { result } = renderHook(() => useLocalStorage('user', null));

    expect(result.current[0]).toEqual(initialUser);

    const updatedUser = { ...initialUser, age: 31 };
    act(() => {
      result.current[1](updatedUser);
    });
    expect(result.current[0]).toEqual(updatedUser);
    expect(localStorageMock.setItem).toHaveBeenCalledWith('user', JSON.stringify(updatedUser));
  });
});

