import { useState, useCallback } from 'react';

/**
 * A custom hook to manage a numerical counter state.
 * @param {number} initialValue The initial value of the counter.
 * @returns {{count: number, increment: () => void, decrement: () => void, reset: () => void}}
 */
function useCounter(initialValue = 0) {
  const [count, setCount] = useState(initialValue);

  const increment = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []);

  const decrement = useCallback(() => {
    setCount(prevCount => prevCount - 1);
  }, []);

  const reset = useCallback(() => {
    setCount(initialValue);
  }, [initialValue]);

  return { count, increment, decrement, reset };
}

import { renderHook, act } from '@testing-library/react-hooks';
import { useCounter } from './useCounter'; // Assuming useCounter is in useCounter.js

test('should increment counter', () => {
  const { result } = renderHook(() => useCounter(0));

  act(() => {
    result.current.increment();
  });

  expect(result.current.count).toBe(1);
});

test('should decrement counter', () => {
  const { result } = renderHook(() => useCounter(10));

  act(() => {
    result.current.decrement();
  });

  expect(result.current.count).toBe(9);
});

test('should reset counter to initial value', () => {
  const { result } = renderHook(() => useCounter(5));
  act(() => {
    result.current.increment();
    result.current.increment();
  });
  expect(result.current.count).toBe(7);

  act(() => {
    result.current.reset();
  });
  expect(result.current.count).toBe(5);
});

import React from 'react';

class PureCounter extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  increment = () => {
    this.setState({ count: this.state.count + 1 });
  }

  render() {
    console.log('PureCounter render called'); // This won't log on parent re-renders unless props/state change
    return (
      <div>
        <h2>Count: {this.state.count}</h2>
        <button onClick={this.increment}>Increment</button>
      </div>
    );
  }
}

// Regular component will re-render on every parent update
class RegularCounter extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  increment = () => {
    this.setState({ count: this.state.count + 1 });
  }

  render() {
    console.log('RegularCounter render called'); // This will log on every parent re-render
    return (
      <div>
        <h2>Count: {this.state.count}</h2>
        <button onClick={this.increment}>Increment</button>
      </div>
    );
  }
}

class ParentComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { parentValue: 0 };
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState(prevState => ({ parentValue: prevState.parentValue + 1 }));
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    console.log('ParentComponent render called');
    return (
      <div style={{ padding: '20px', border: '1px solid #ccc' }}>
        <h1>Parent Value: {this.state.parentValue}</h1>
        <p>PureCounter below will only re-render if its own state changes.</p>
        <PureCounter />
        <hr/>
        <p>RegularCounter below will re-render every second with the parent.</p>
        <RegularCounter />
      </div>
    );
  }
}

