class ParentWithImmutableData extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      tick: 0,
      item: { id: 1, value: 'initial' }
    };
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState(prevState => ({
        tick: prevState.tick + 1,
        // Correct way to update nested data for PureComponent:
        // Always create a new object reference if content changes.
        item: prevState.tick % 2 === 0 
              ? { ...prevState.item, value: `updated-${prevState.tick}` } // New object reference
              : prevState.item // Same object reference if value not changing
      }));
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
