import { useState, useCallback } from 'react';
import { renderHook, act } from '@testing-library/react-hooks';

/**
 * A custom hook to manage a boolean toggle state.
 * @param {boolean} initialValue The initial boolean value (defaults to false).
 * @returns {[boolean, () => void]}
 */
function useToggle(initialValue = false) {
  const [value, setValue] = useState(initialValue);

  const toggle = useCallback(() => {
    setValue(prevValue => !prevValue);
  }, []);

  return [value, toggle];
}

test('should initialize with default false', () => {
  const { result } = renderHook(() => useToggle());
  expect(result.current[0]).toBe(false);
});

test('should initialize with provided initial value', () => {
  const { result } = renderHook(() => useToggle(true));
  expect(result.current[0]).toBe(true);
});

test('should toggle value correctly from false to true and back', () => {
  const { result } = renderHook(() => useToggle());
  expect(result.current[0]).toBe(false);

  act(() => {
    result.current[1](); // Call toggle function
  });
  expect(result.current[0]).toBe(true);

  act(() => {
    result.current[1](); // Call toggle again
  });
  expect(result.current[0]).toBe(false);
});

test('should toggle value correctly from true to false and back', () => {
  const { result } = renderHook(() => useToggle(true));
  expect(result.current[0]).toBe(true);

  act(() => {
    result.current[1](); // Call toggle function
  });
  expect(result.current[0]).toBe(false);

  act(() => {
    result.current[1](); // Call toggle again
  });
  expect(result.current[0]).toBe(true);
});

test('should handle multiple rapid toggles correctly', () => {
  const { result } = renderHook(() => useToggle(false));
  expect(result.current[0]).toBe(false);

  act(() => {
    result.current[1](); // false -> true
    result.current[1](); // true -> false
    result.current[1](); // false -> true
  });
  expect(result.current[0]).toBe(true);
});

