import React, { useState, useEffect } from 'react';

function useLocalStorage<T>(key: string, initialValue: T): [T, React.Dispatch<React.SetStateAction<T>>] {
  // Function to get the stored value from localStorage
  const getStoredValue = (): T => {
    if (typeof window === 'undefined') { // Check for server-side rendering environment
      return initialValue;
    }
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(`Error reading from localStorage key "${key}":`, error);
      return initialValue;
    }
  };

  const [value, setValue] = useState<T>(getStoredValue);

  useEffect(() => {
    if (typeof window !== 'undefined') {
      try {
        window.localStorage.setItem(key, JSON.stringify(value));
      } catch (error) {
        console.error(`Error writing to localStorage key "${key}":`, error);
      }
    }
  }, [key, value]);

  return [value, setValue];
}

// --- Example Usage (for testing) ---
function Counter() {
  const [count, setCount] = useLocalStorage('my-counter', 0);

  return (
    <div>
      <h3>Counter with LocalStorage</h3>
      <p>Count: {count}</p>
      <button onClick={() => setCount(prevCount => prevCount + 1)}>Increment</button>
      <button onClick={() => setCount(0)}>Reset</button>
    </div>
  );
}

function UserSettings() {
  const [name, setName] = useLocalStorage('user-name', 'Guest');
  const [email, setEmail] = useLocalStorage('user-email', '');
