import React, { useState, useEffect } from 'react';

function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    // Set a timeout to update the debounced value after the specified delay
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    // Clean up the timeout if value or delay changes, or if the component unmounts
    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]); // Only re-run if value or delay changes

  return debouncedValue;
}

// --- Example Usage (for testing) ---
function SearchInput() {
  const [searchTerm, setSearchTerm] = useState('');
  const debouncedSearchTerm = useDebounce(searchTerm, 500); // Debounce by 500ms
  const [apiCallCount, setApiCallCount] = useState(0);

  useEffect(() => {
    // This effect should only run AFTER the debounce delay
    if (debouncedSearchTerm) {
      console.log('Performing API search for:', debouncedSearchTerm);
      setApiCallCount(prev => prev + 1);
      // Simulate an API call
      // fetch(`/api/search?q=${debouncedSearchTerm}`).then(res => res.json());
    } else if (searchTerm === '') {
      console.log('Search term cleared.');
      setApiCallCount(0);
    }
  }, [debouncedSearchTerm]);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h2>Search with Debounce Hook</h2>
      <input
        type="text"
        placeholder="Type to search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ padding: '10px', fontSize: '1em', width: '300px', marginBottom: '10px' }}
      />
      <p>Current input: <strong>{searchTerm}</strong></p>
      <p>Debounced search term (updates after 500ms pause): <strong>{debouncedSearchTerm}</strong></p>
      <p>Simulated API calls made: <strong>{apiCallCount}</strong></p>
      <p style={{ color: '#888', fontSize: '0.9em' }}>Type quickly, then pause to see the debounced term update.</p>
