  return (
    <div style={{ background: theme === 'dark' ? '#333' : '#fff', color: theme === 'dark' ? '#fff' : '#333', padding: '20px', borderRadius: '8px' }}>
      <h2>Persistent Counter & Theme</h2>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>Increment</button>
      <button onClick={() => setCount(prevCount => prevCount - 1)}>Decrement</button>
      <button onClick={() => setCount(0)}>Reset Count</button>

      <p>Current Theme: {theme}</p>
      <button onClick={toggleTheme}>Toggle Theme</button>
    </div>
  );
}

export default PersistentCounter;

import { useState, useEffect } from 'react';

interface FetchOptions extends RequestInit {}

interface FetchResult<T> {
  data: T | null;
  loading: boolean;
  error: string | null;
}

// Custom hook for fetching data
function useFetch<T>(url: string, options?: FetchOptions): FetchResult<T> {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    let isMounted = true; // Flag to track if the component is mounted
    setLoading(true); // Set loading to true at the start of fetch
    setError(null); // Clear previous errors

    const fetchData = async () => {
      try {
        const response = await fetch(url, options); // Perform fetch request
        if (!response.ok) { // Check for HTTP errors (e.g., 404, 500)
          throw new Error(`HTTP error! Status: ${response.status} - ${response.statusText}`);
        }

        const json: T = await response.json(); // Parse response as JSON
        if (isMounted) { // Only update state if component is still mounted
          setData(json);
          setError(null);
        }
      } catch (e) {
        const errorMessage = (e instanceof Error) ? e.message : String(e);
        if (isMounted) { // Only update state if component is still mounted
          setError(errorMessage);
          setData(null);
        }
      } finally {
        if (isMounted) { // Only update state if component is still mounted
          setLoading(false);
        }
      }
    };

    fetchData(); // Execute the fetch operation

    // Cleanup function: runs when component unmounts or before effect re-runs
    return () => {
      isMounted = false;
    };
  }, [url, options]); // Dependencies: re-run when url or options change

  return { data, loading, error };
}

