import React, { useState, useEffect } from 'react';

function RegistrationForm() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [usernameError, setUsernameError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  // Effect to validate username whenever it changes
  useEffect(() => {
    if (username.length > 0 && username.length < 3) {
      setUsernameError('Username must be at least 3 characters.');
    } else {
      setUsernameError('');
    }
  }, [username]);

  // Effect to validate email whenever it changes
  useEffect(() => {
    const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    if (email.length > 0 && !emailRegex.test(email)) {
      setEmailError('Please enter a valid email address.');
    } else {
      setEmailError('');
    }
  }, [email]);

  // Effect to validate password whenever it changes
  useEffect(() => {
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{6,}$/;
    if (password.length > 0 && !passwordRegex.test(password)) {
      setPasswordError('Password must be at least 6 characters, contain one uppercase, one lowercase, and one number.');
    } else {
      setPasswordError('');
    }
  }, [password]);

  const validateAllFields = () => {
    let isValid = true;

    if (username.length < 3) { setUsernameError('Username must be at least 3 characters.'); isValid = false; }
    else { setUsernameError(''); }

    const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    if (!emailRegex.test(email)) { setEmailError('Please enter a valid email address.'); isValid = false; }
    else { setEmailError(''); }

    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{6,}$/;
    if (!passwordRegex.test(password)) { setPasswordError('Password must be at least 6 characters, contain one uppercase, one lowercase, and one number.'); isValid = false; }
    else { setPasswordError(''); }

    return isValid;
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // Validate all fields on submit, in case user didn't trigger all 'onChange' validations
    if (validateAllFields()) {
      console.log('Form Submitted:', { username, email, password });
      alert('Registration successful!');
      // Reset form
      setUsername('');
      setEmail('');
      setPassword('');
    } else {
      console.log('Form has errors. Please fix them.');
    }
  };

  // Check if all fields have non-empty values AND no current errors
  const isFormValid = username.length > 0 && email.length > 0 && password.length > 0 &&
                      !usernameError && !emailError && !passwordError;

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '400px', margin: 'auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Register</h2>
      <div>
        <label htmlFor="reg-username">Username:</label>
        <input 
          type="text" 
          id="reg-username"
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          onBlur={validateAllFields} // Validate on blur for immediate feedback
        />
        {usernameError && <p style={{ color: 'red', fontSize: '0.8em' }}>{usernameError}</p>}
      </div>
      <div>
        <label htmlFor="reg-email">Email:</label>
        <input 
          type="email" 
          id="reg-email"
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          onBlur={validateAllFields}
        />
        {emailError && <p style={{ color: 'red', fontSize: '0.8em' }}>{emailError}</p>}
      </div>
      <div>
        <label htmlFor="reg-password">Password:</label>
        <input 
          type="password" 
          id="reg-password"
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          onBlur={validateAllFields}
        />
        {passwordError && <p style={{ color: 'red', fontSize: '0.8em' }}>{passwordError}</p>}
      </div>
      <button type="submit" disabled={!isFormValid}>Register</button>
    </form>
  );
}

export default RegistrationForm;

