import { useState, useEffect, useCallback, useRef } from 'react';

function useFetchWithRefetchAndAbort(url, options) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const controllerRef = useRef(null); // To store AbortController

  const fetchData = useCallback(async () => {
    // Abort any previous request if active
    if (controllerRef.current) {
      controllerRef.current.abort();
    }
    const controller = new AbortController();
    controllerRef.current = controller;
    const signal = controller.signal;

    setLoading(true);
    setError(null);
    try {
      const response = await fetch(url, { ...options, signal });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      setData(result);
    } catch (e) {
      if (e.name === 'AbortError') {
        console.log('Fetch aborted');
        return; // Don't set error for aborted requests
      }
      setError(e.message);
    } finally {
      setLoading(false);
      controllerRef.current = null; // Clear controller once request is done/aborted
    }
  }, [url, options]); // fetchData itself is memoized

  useEffect(() => {
    fetchData(); // Initial fetch and re-fetch on url/options change

    return () => {
      // Abort request on component unmount or when effect re-runs
      if (controllerRef.current) {
        controllerRef.current.abort();
      }
    };
  }, [fetchData]); // Dependency on memoized fetchData

  return { data, loading, error, refetch: fetchData };
}

