import { useState, useEffect, useRef, useCallback } from 'react';

// A simple in-memory cache map
const cache = new Map();

function useFetchEnhanced(url, options = {}, cacheDuration = 300000) { // cacheDuration in ms (5 minutes default)
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const abortControllerRef = useRef(null);

  const fetchData = useCallback(async (retries = 0) => {
    if (!url) {
      setData(null);
      setLoading(false);
      setError(null);
      return;
    }

    // 1. Abort any ongoing request
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    const controller = new AbortController();
    abortControllerRef.current = controller;
    const signal = controller.signal;

    // 2. Check cache for GET requests
    if (options.method === 'GET' || !options.method) {
      const cached = cache.get(url);
      if (cached && (Date.now() - cached.timestamp < cacheDuration)) {
        setData(cached.data);
        setLoading(false);
        setError(null);
        console.log(`Cache hit for ${url}`);
        return; // Return cached data instantly
      }
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(url, { ...options, signal });

      if (!response.ok) {
        // Handle HTTP errors for retry
        if (response.status >= 500 && retries < 3) {
          const retryDelay = [500, 1000, 2000][retries];
          console.warn(`Fetch failed for ${url}, retrying in ${retryDelay}ms... (Attempt ${retries + 1})`);
          await new Promise(resolve => setTimeout(resolve, retryDelay));
          return fetchData(retries + 1); // Retry recursively
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }

