      }

    } catch (e) {
      if (e.name === 'AbortError') {
        console.log('Fetch aborted');
        return; // Do not set error for aborted requests
      }
      // Handle network errors for retry
      if (e instanceof TypeError && e.message === 'Failed to fetch' && retries < 3) {
