        console.warn(`Network error for ${url}, retrying in ${retryDelay}ms... (Attempt ${retries + 1})`);
        await new Promise(resolve => setTimeout(resolve, retryDelay));
        return fetchData(retries + 1); // Retry recursively
      }
      setError(e.message);
    } finally {
      setLoading(false);
      abortControllerRef.current = null; // Clear controller once request is done/aborted
    }
  }, [url, options, cacheDuration]); // Dependencies for fetchData

  useEffect(() => {
    let isMounted = true;
    // Helper function to call fetchData and ensure state updates only on mounted component
    const safeFetch = async () => {
      await fetchData();
      // No explicit isMounted checks needed for setData/setError/setLoading
      // because fetchData itself directly sets state only if not aborted.
      // However, for the initial state transition (loading=true), we do it outside.
