import { useState, useEffect, useRef, useCallback } from 'react';

// A simple in-memory cache map
const cache = new Map();

function useFetchEnhanced(url, options = {}, cacheDuration = 300000) { // cacheDuration in ms (5 minutes default)
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const abortControllerRef = useRef(null); // Ref to store AbortController instance

  // Using useCallback to memoize fetchData to prevent infinite loops in useEffect dependencies
  const fetchData = useCallback(async (retries = 0) => {
    // If URL is invalid, reset state and exit
    if (!url) {
      setData(null);
      setLoading(false);
      setError(null);
      return;
    }

    // Abort any ongoing request before starting a new one
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    const controller = new AbortController();
    abortControllerRef.current = controller;
    const signal = controller.signal;

    // Implement in-memory caching for GET requests
    if (options.method === 'GET' || !options.method) {
      const cached = cache.get(url);
      if (cached && (Date.now() - cached.timestamp < cacheDuration)) {
        setData(cached.data);
        setLoading(false);
        setError(null);
        console.log(`useFetchEnhanced: Cache hit for ${url}`);
        return; // Return cached data instantly
      }
    }

    setLoading(true); // Start loading
    setError(null); // Clear previous errors

    try {
      const response = await fetch(url, { ...options, signal });

      // Check for HTTP errors (e.g., 5xx for retries, others for immediate error)
      if (!response.ok) {
        // Retry mechanism for server errors (5xx) and network errors
        if (response.status >= 500 && retries < 3) {
          const retryDelay = [500, 1000, 2000][retries];
          console.warn(`useFetchEnhanced: HTTP Error ${response.status} for ${url}, retrying in ${retryDelay}ms... (Attempt ${retries + 1})`);
          await new Promise(resolve => setTimeout(resolve, retryDelay));
          return fetchData(retries + 1); // Recursive retry
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      setData(result);

      // Cache successful GET requests with a timestamp and auto-clear
      if (options.method === 'GET' || !options.method) {
        cache.set(url, { data: result, timestamp: Date.now() });
        // Set a timeout to clear this specific cache entry after cacheDuration
        setTimeout(() => {
          if (cache.get(url) && cache.get(url).timestamp === cache.get(url).timestamp) { // Only clear if it's the same entry
            cache.delete(url);
            console.log(`useFetchEnhanced: Cache cleared for ${url}`);
          }
        }, cacheDuration);
      }

    } catch (e) {
      if (e.name === 'AbortError') {
        console.log('useFetchEnhanced: Fetch aborted');
        return; // Do not set error or loading state for aborted requests
      }

      // Retry mechanism for network errors (e.g., 'Failed to fetch')
      if (e instanceof TypeError && (e.message === 'Failed to fetch' || e.message.includes('network')) && retries < 3) {
        const retryDelay = [500, 1000, 2000][retries];
        console.warn(`useFetchEnhanced: Network error for ${url}, retrying in ${retryDelay}ms... (Attempt ${retries + 1})`);
        await new Promise(resolve => setTimeout(resolve, retryDelay));
        return fetchData(retries + 1); // Recursive retry
      }
      setError(e.message);
    } finally {
      setLoading(false); // Stop loading regardless of success or failure
      abortControllerRef.current = null; // Clear controller ref as request is complete or aborted
    }
  }, [url, options, cacheDuration]); // Dependencies for useCallback

  // useEffect to trigger fetch on mount and when dependencies (url, options) change
  useEffect(() => {
    // No `isMounted` flag needed with AbortController if state updates are always guarded by `if (!signal.aborted) { ... }`
    // However, the `isMounted` pattern is safer if you're not fully certain all async paths respect `signal.aborted`.
    // In this specific implementation, all state updates are within the `fetchData` function,
    // which is guarded by `if (e.name === 'AbortError') return;`, so it's implicitly handled.

    fetchData(); // Execute fetch logic

    // Cleanup function: Abort ongoing request if component unmounts or dependencies change
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, [fetchData]); // Dependency on the memoized fetchData function

  return { data, loading, error };
}

