import { useState, useEffect, useMemo } from 'react';

const defaultBreakpoints = {
  xs: 0,
  sm: 600,
  md: 960,
  lg: 1280,
  xl: 1920,
};

function useBreakpoints(breakpoints = defaultBreakpoints) {
  // Memoize sorted breakpoints to avoid re-sorting on every render
  const sortedBreakpoints = useMemo(() => 
    Object.entries(breakpoints)
      .sort(([, valA], [, valB]) => valA - valB)
  , [breakpoints]);

  const getBreakpointState = useCallback(() => {
    if (typeof window === 'undefined') {
      // Default state for SSR: assume 'xs' matched, all others false.
      // This provides a consistent initial render that can then hydrate.
      const initialMatches = {};
      sortedBreakpoints.forEach(([key]) => {
        initialMatches[key] = (key === 'xs'); // Assuming 'xs' is 0 or smallest
      });
      return {
        matches: initialMatches,
        current: 'xs' 
      };
    }

    const currentWidth = window.innerWidth;
    let currentBreakpoint = 'xs'; // Initialize with the smallest breakpoint name
    const matches = {};

    // Iterate through sorted breakpoints to determine matches and the largest matched breakpoint
    for (let i = 0; i < sortedBreakpoints.length; i++) {
      const [name, value] = sortedBreakpoints[i];
      if (currentWidth >= value) {
        matches[name] = true;
        currentBreakpoint = name; // Update currentBreakpoint if this one matches
      } else {
        matches[name] = false;
      }
    }
    return { matches, current: currentBreakpoint };
  }, [sortedBreakpoints]); // getBreakpointState depends on sortedBreakpoints

  // Use lazy initializer for initial state, and then update via effect
  const [state, setState] = useState(getBreakpointState);

  useEffect(() => {
    // Only run event listener logic on the client-side
    if (typeof window === 'undefined') {
      return;
    }

    const handleResize = () => {
      setState(getBreakpointState());
    };

    // Add event listener for window resize
    window.addEventListener('resize', handleResize);

    // Initial state update on client-side might be needed if SSR default was wrong
    // or for clients where JS is enabled before hydration.
    // setState(getBreakpointState()); // Can cause hydration mismatch if not carefully handled with specific SSR patterns
                                    // Better to rely on initial lazy state and event listener for subsequent changes.

    // Cleanup function: remove event listener
    return () => window.removeEventListener('resize', handleResize);
  }, [getBreakpointState]); // Effect depends on memoized getBreakpointState

  // Return individual breakpoint flags and the current breakpoint name
  return { ...state.matches, current: state.current };
}

