  const [results, setResults] = useState<{ id: number; name: string }[]>([]);

  // Effect to debounce searchTerm
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 500); // 500ms debounce delay

    // Cleanup function: clear timeout if searchTerm changes before delay
    return () => {
      clearTimeout(handler);
    };
  }, [searchTerm]); // Re-run effect whenever searchTerm changes

  // Effect to fetch results when debouncedSearchTerm changes
  useEffect(() => {
    if (debouncedSearchTerm) {
      fetchSearchResults(debouncedSearchTerm).then(results => {
        setResults(results);
      });
    } else {
      setResults([]);
    }
  }, [debouncedSearchTerm]); // Re-run effect whenever debouncedSearchTerm changes

  return (
    <div>
      <input
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        placeholder="Search..."
      />
      <ul>
        {results.map(result => (
