        ))}
      </ul>
    </div>
  );
}

import React, { createContext, useReducer, useContext } from 'react';

// 1. Create Context
const TodoContext = createContext<{
  todos: { id: number; text: string; completed: boolean }[];
  dispatch: React.Dispatch<any>;
} | undefined>(undefined);

// 2. Action Types (constants to avoid typos)
const ADD_TODO = 'ADD_TODO';
const TOGGLE_TODO = 'TOGGLE_TODO';
const DELETE_TODO = 'DELETE_TODO';

// 3. Reducer Function
interface TodoState { id: number; text: string; completed: boolean }[];
interface TodoAction { type: string; payload?: any; }

function todoReducer(state: TodoState, action: TodoAction): TodoState {
  switch (action.type) {
    case ADD_TODO:
      return [...state, { id: Date.now(), text: action.payload, completed: false }];
    case TOGGLE_TODO:
      return state.map(todo =>
        todo.id === action.payload ? { ...todo, completed: !todo.completed } : todo
      );
    case DELETE_TODO:
      return state.filter(todo => todo.id !== action.payload);
    default:
      return state;
  }
}

// 4. Provider Component
function TodoProvider({ children }: { children: React.ReactNode }) {
  const [todos, dispatch] = useReducer(todoReducer, []); // Initialize with an empty array

  return (
    <TodoContext.Provider value={{ todos, dispatch }}>
      {children}
    </TodoContext.Provider>
  );
}

// 5. Custom Hook to Consume Context
function useTodo() {
  const context = useContext(TodoContext);
  if (context === undefined) {
    throw new Error('useTodo must be used within a TodoProvider');
  }
  return context;
}

// 6. Components that use the context
function TodoList() {
  const { todos, dispatch } = useTodo();

  return (
    <ul>
