}

function ResetButton() {
  const { dispatch } = useCounter();
  return <button onClick={() => dispatch({ type: RESET })}>Reset</button>;
}

// App component
function App() {
  return (
    <CounterProvider>
      <h1>Global Counter</h1>
      <CounterDisplay />
      <IncrementButton />
      <DecrementButton />
      <ResetButton />
    </CounterProvider>
