import React, { useState, useEffect } from 'react';

// Reusable custom hook for debouncing any value
function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    // Set a timeout to update the debounced value
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    // Cleanup function: clear timeout if value or delay changes before timeout fires
    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]); // Re-run effect only if value or delay changes

  return debouncedValue;
}

function DebouncedSearch() {
  const [searchTerm, setSearchTerm] = useState('');

  // Use the custom useDebounce hook
  const debouncedSearchTerm = useDebounce(searchTerm, 500);

  // Optional: Effect to simulate fetching results when debounced term changes
  useEffect(() => {
    if (debouncedSearchTerm) {
      console.log(`Performing search for: "${debouncedSearchTerm}"`);
      // In a real app: fetchSearchResults(debouncedSearchTerm).then(results => ...);
    } else {
      console.log('Debounced search term is empty, clearing results.');
    }
  }, [debouncedSearchTerm]); // This effect runs only when debouncedSearchTerm changes

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
