const TOGGLE_TODO = 'TOGGLE_TODO';
const REMOVE_TODO = 'REMOVE_TODO';

function todoReducer(state, action) {
  switch (action.type) {
    case ADD_TODO:
      return [
        ...state,
        {
          id: Date.now(), // Simple unique ID
          text: action.payload,
          completed: false,
        },
      ];
    case TOGGLE_TODO:
      return state.map(todo =>
        todo.id === action.payload ? { ...todo, completed: !todo.completed } : todo
      );
    case REMOVE_TODO:
      return state.filter(todo => todo.id !== action.payload);
    default:
      return state;
  }
}

import React, { createContext, useReducer, useContext } from 'react';

// 1. Define Context
const TodoContext = createContext(null);

// 2. Define Reducer (as shown in previous example)
const ADD_TODO = 'ADD_TODO';
const TOGGLE_TODO = 'TOGGLE_TODO';
const REMOVE_TODO = 'REMOVE_TODO';

function todoReducer(state, action) {
  switch (action.type) {
    case ADD_TODO:
      return [
        ...state,
        {
          id: Date.now(),
          text: action.payload,
          completed: false,
        },
      ];
    case TOGGLE_TODO:
      return state.map(todo =>
        todo.id === action.payload ? { ...todo, completed: !todo.completed } : todo
      );
    case REMOVE_TODO:
      return state.filter(todo => todo.id !== action.payload);
    default:
      return state;
  }
}

// 3. Implement Provider Component
export function TodoProvider({ children }) {
  const [todos, dispatch] = useReducer(todoReducer, []); // Initial state is an empty array

  return (
    <TodoContext.Provider value={{ todos, dispatch }}>
      {children}
    </TodoContext.Provider>
  );
}

// 4. Create Custom Hook to consume context
export function useTodo() {
  const context = useContext(TodoContext);
  if (context === null) {
    throw new Error('useTodo must be used within a TodoProvider');
  }
  return context;
}

import React from 'react';
import { useTodo, ADD_TODO, TOGGLE_TODO, REMOVE_TODO } from './TodoContext'; // Assuming previous file is TodoContext.js

// TodoItem component
function TodoItem({ todo }) {
  const { dispatch } = useTodo();

  return (
    <li>
      <span
        style={{ textDecoration: todo.completed ? 'line-through' : 'none', cursor: 'pointer' }}
        onClick={() => dispatch({ type: TOGGLE_TODO, payload: todo.id })}
      >
        {todo.text}
      </span>
      <button onClick={() => dispatch({ type: REMOVE_TODO, payload: todo.id })}>Remove</button>
    </li>
  );
}

// TodoList component
function TodoList() {
  const { todos } = useTodo();

  return (
    <ul>
      {todos.map(todo => (
        <TodoItem key={todo.id} todo={todo} />
      ))}
    </ul>
  );
}

// AddTodo component
function AddTodo() {
  const { dispatch } = useTodo();
  const [text, setText] = React.useState('');

  const handleSubmit = e => {
    e.preventDefault();
    if (text.trim()) {
      dispatch({ type: ADD_TODO, payload: text });
      setText('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Add todo..."
      />
      <button type="submit">Add</button>
    </form>
  );
}

// App component using the custom hook pattern
function TodoApp() {
  return (
    <TodoProvider>
      <h1>Todo App</h1>
      <AddTodo />
      <TodoList />
    </TodoProvider>
  );
}

