import React, { useState, useRef, useEffect } from 'react';
import ReactDOM from 'react-dom';

const Tooltip = ({ children, targetElement }) => {
  const [isVisible, setIsVisible] = useState(false);
  const tooltipRoot = useRef(null);

  useEffect(() => {
    tooltipRoot.current = document.getElementById('tooltip-root');
  }, []);

  useEffect(() => {
    const target = targetElement?.current;
    if (!target) return;

    const showTooltip = () => setIsVisible(true);
    const hideTooltip = () => setIsVisible(false);

    target.addEventListener('mouseenter', showTooltip);
    target.addEventListener('mouseleave', hideTooltip);

    return () => {
      target.removeEventListener('mouseenter', showTooltip);
      target.removeEventListener('mouseleave', hideTooltip);
    };
  }, [targetElement]);

  if (!tooltipRoot.current || !isVisible) {
    return null;
  }

  // Basic positioning - in a real app, this would be more complex
  const targetRect = targetElement.current.getBoundingClientRect();
  const style = {
    position: 'absolute',
    top: `${targetRect.bottom + window.scrollY + 5}px`,
    left: `${targetRect.left + window.scrollX}px`,
    backgroundColor: 'black',
    color: 'white',
    padding: '5px 10px',
    borderRadius: '4px',
    zIndex: 9999,
  };

  return ReactDOM.createPortal(
    <div style={style}>{children}</div>,
    tooltipRoot.current
  );
};

