import React, { createContext, useReducer, useContext, useState, useRef, useEffect } from 'react';

// Action Types
const ADD_TODO = 'ADD_TODO';
const TOGGLE_TODO = 'TOGGLE_TODO';
const DELETE_TODO = 'DELETE_TODO'; // Renamed from REMOVE_TODO for clarity
const EDIT_TODO = 'EDIT_TODO';

// Reducer
function todoReducer(state, action) {
  switch (action.type) {
    case ADD_TODO:
      return [
        ...state,
        {
          id: Date.now(),
          text: action.payload,
          completed: false,
        },
      ];
    case TOGGLE_TODO:
      return state.map(todo =>
        todo.id === action.payload ? { ...todo, completed: !todo.completed } : todo
      );
    case DELETE_TODO:
      return state.filter(todo => todo.id !== action.payload);
    case EDIT_TODO:
      return state.map(todo =>
        todo.id === action.payload.id ? { ...todo, text: action.payload.newText } : todo
      );
    default:
      return state;
  }
}

// Context & Provider
const TodoContext = createContext(null);

export function TodoProvider({ children }) {
  const [todos, dispatch] = useReducer(todoReducer, []);
  return (
    <TodoContext.Provider value={{ todos, dispatch }}>
      {children}
    </TodoContext.Provider>
  );
}

// Custom Hook
export function useTodo() {
  const context = useContext(TodoContext);
  if (context === null) {
    throw new Error('useTodo must be used within a TodoProvider');
  }
  return context;
}

// TodoItem Component (modified)
function TodoItem({ todo }) {
  const { dispatch } = useTodo();
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);
  const inputRef = useRef(null);

  useEffect(() => {
    if (isEditing) {
      inputRef.current?.focus();
    }
  }, [isEditing]);

  const handleSaveEdit = () => {
    if (editText.trim() && editText !== todo.text) {
      dispatch({ type: EDIT_TODO, payload: { id: todo.id, newText: editText.trim() } });
    }
    setIsEditing(false);
  };

  const handleEditKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSaveEdit();
    } else if (e.key === 'Escape') {
      setEditText(todo.text); // Revert changes
      setIsEditing(false);
    }
  };

  return (
    <li>
      {isEditing ? (
        <input
          ref={inputRef}
          type="text"
          value={editText}
          onChange={e => setEditText(e.target.value)}
          onBlur={handleSaveEdit} // Save on blur
          onKeyDown={handleEditKeyDown}
        />
      ) : (
        <span
          style={{ textDecoration: todo.completed ? 'line-through' : 'none', cursor: 'pointer' }}
          onClick={() => dispatch({ type: TOGGLE_TODO, payload: todo.id })}
        >
          {todo.text}
        </span>
      )}

      {!isEditing && <button onClick={() => setIsEditing(true)}>Edit</button>}
      <button onClick={() => dispatch({ type: DELETE_TODO, payload: todo.id })}>Delete</button>
    </li>
  );
}

// TodoList Component (fine as is)
function TodoList() {
  const { todos } = useTodo();
  return (
    <ul>
      {todos.map(todo => (
        <TodoItem key={todo.id} todo={todo} />
      ))}
    </ul>
  );
}

// AddTodo Component (fine as is)
function AddTodo() {
  const { dispatch } = useTodo();
  const [text, setText] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    if (text.trim()) {
      dispatch({ type: ADD_TODO, payload: text });
      setText('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Add todo..."
      />
      <button type="submit">Add</button>
    </form>
  );
}

// App Component
export default function TodoApp() {
  return (
    <TodoProvider>
      <h1>Todo App</h1>
      <AddTodo />
      <TodoList />
    </TodoProvider>
  );
}

