import React, { useEffect, useRef, useCallback } from 'react';
import ReactDOM from 'react-dom';

function Modal({ isOpen, onClose, children }) {
  const modalRef = useRef(null); // Ref for the modal content div itself
  const previouslyActiveElement = useRef(null); // For focus management

  // Store the portal root element in a ref to avoid re-querying DOM
  const portalRoot = useRef(null);
  useEffect(() => {
    portalRoot.current = document.getElementById('modal-root');
  }, []);

  // Function to handle escape key press
  const handleEscapeKey = useCallback((event) => {
    if (event.key === 'Escape' && isOpen) {
      onClose();
    }
  }, [isOpen, onClose]);

  // Function to handle click outside modal content
  const handleOverlayClick = useCallback((event) => {
    if (modalRef.current && !modalRef.current.contains(event.target)) {
      onClose();
    }
  }, [onClose]);

  useEffect(() => {
    if (isOpen) {
      // Save reference to the element that was focused before modal opened
      previouslyActiveElement.current = document.activeElement;
      document.addEventListener('keydown', handleEscapeKey);
      // Focus the modal content for accessibility
      modalRef.current?.focus(); 
    } else {
      document.removeEventListener('keydown', handleEscapeKey);
      // Restore focus to the previously active element when modal closes
      if (previouslyActiveElement.current) {
        previouslyActiveElement.current.focus();
      }
    }
    // Cleanup event listeners on component unmount or when isOpen changes
    return () => {
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, [isOpen, handleEscapeKey]);

  if (!isOpen || !portalRoot.current) {
    return null;
  }

  return ReactDOM.createPortal(
    <div className="modal-overlay" onClick={handleOverlayClick} tabIndex="-1">
      <div
        className="modal-content"
        ref={modalRef}
        role="dialog"
        aria-modal="true"
        tabIndex="0" // Make content focusable for initial focus
        onClick={e => e.stopPropagation()} // Prevent clicks inside content from closing modal
      >
        {children}
        <button className="modal-close-button" onClick={onClose} aria-label="Close modal">&times;</button>
      </div>
    </div>,
    portalRoot.current
  );
}

// Basic CSS (add this to your index.css or a style tag)
/*
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
