import React, { useState } from 'react';
import ReactDOM from 'react-dom';

// 1. Create a dedicated DOM node in public/index.html:
// <div id="modal-root"></div>

// 2. Define a reusable Modal component using createPortal
function Modal({ children, onClose }) {
  const modalRoot = document.getElementById('modal-root');
  if (!modalRoot) {
    // In a real application, you might throw an error or handle this more gracefully
    console.error("The DOM element with ID 'modal-root' was not found.");
    return null;
  }
  return ReactDOM.createPortal(
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        {children}
        <button onClick={onClose}>Close Modal</button>
      </div>
    </div>,
    modalRoot
  );
}

// 3. Use the Modal component in your App
function App() {
  const [showModal, setShowModal] = useState(false);

  return (
