import React, { useState } from 'react';

// --- ErrorBoundary Component ---
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render shows the fallback UI.
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service here.
    console.error("Caught by ErrorBoundary:", error, errorInfo);
    this.setState({
      error: error,
      errorInfo: errorInfo
    });
  }

  render() {
    if (this.state.hasError) {
      // Render any custom fallback UI.
      return (
        <div style={{ border: '1px solid red', padding: '10px', margin: '10px', backgroundColor: '#ffe6e6' }}>
          <h2>Something went wrong in this section.</h2>
          <p>We are sorry for the inconvenience.</p>
          {/* Display error details in development for debugging */}
          {this.state.error && this.state.errorInfo && (
            <details style={{ whiteSpace: 'pre-wrap', fontSize: '0.8em', color: '#cc0000' }}>
              {this.state.error.toString()}
              <br />
              {this.state.errorInfo.componentStack}
            </details>
          )}
        </div>
      );
    }
    return this.props.children;
  }
}

// --- Buggy Component ---
function BuggyComponent() {
  const [count, setCount] = useState(0);

  const handleClick = () => {
    setCount(prevCount => prevCount + 1);
  };

  if (count === 5) {
    // This error will be caught by the ErrorBoundary if wrapped
    throw new Error('BuggyComponent crashed at count 5!');
  }

  return (
    <div style={{ border: '1px solid gray', padding: '10px', margin: '10px', backgroundColor: '#f9f9f9' }}>
      <p>Current Count: {count}</p>
      <button onClick={handleClick}>Increment Counter</button>
      <p>Keep clicking to see the error!</p>
    </div>
  );
}

// --- App Component ---
function App() {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '800px', margin: '20px auto', padding: '20px', border: '1px solid #eee', boxShadow: '2px 2px 5px rgba(0,0,0,0.1)' }}>
      <h1 style={{ textAlign: 'center' }}>React Error Boundary Demonstration</h1>
      <p>This example demonstrates how Error Boundaries help prevent an entire application crash. The first counter is wrapped in an Error Boundary, while the second is not.</p>

      <h2 style={{ color: '#0056b3' }}>Wrapped Component:</h2>
      <ErrorBoundary>
        <BuggyComponent />
      </ErrorBoundary>

      <hr style={{ margin: '30px 0' }}/>

      <h2 style={{ color: '#cc0000' }}>Unwrapped Component (will crash the app):</h2>
      <BuggyComponent />

      <p style={{ marginTop: '40px', fontSize: '0.9em', color: '#666' }}>Note: The unwrapped component will cause the entire React application to unmount and display a blank page or a development error overlay when it crashes.</p>
    </div>
  );
}

export default App;

