import React, { useState, useEffect } from 'react';

// --- StrictModeDemoComponent (Class Component) ---
class StrictModeDemoComponent extends React.Component {
  constructor(props) {
    super(props);
    console.log('StrictModeDemoComponent: Constructor called (should be double-invoked in StrictMode)');
    this.state = { count: 0 };
  }

  // This lifecycle method is considered unsafe and will cause a warning in StrictMode.
  UNSAFE_componentWillMount() {
    console.log('StrictModeDemoComponent: UNSAFE_componentWillMount called (Strict Mode Warning!)');
  }

  componentDidMount() {
    console.log('StrictModeDemoComponent: componentDidMount called (should be single-invoked)');
  }

  render() {
    console.log('StrictModeDemoComponent: Render called (should be double-invoked in StrictMode)');
    return (
      <div style={{ border: '1px dashed blue', padding: '10px', margin: '10px', backgroundColor: '#e6f0ff' }}>
        <h3>Class Component Demo</h3>
        <p>Check console for logs.</p>
        <p>Count: {this.state.count}</p>
        <button onClick={() => this.setState(prev => ({ count: prev.count + 1 }))}>Increment (Affects Render)</button>
      </div>
    );
  }
}

// --- FunctionalStrictModeDemo (Functional Component) ---
function FunctionalStrictModeDemo() {
  console.log('FunctionalStrictModeDemo: Component body (render) called (should be double-invoked in StrictMode)');

  useEffect(() => {
    console.log('FunctionalStrictModeDemo: useEffect (mount) called (should be single-invoked)');
    // This should only run once on mount
    return () => {
      console.log('FunctionalStrictModeDemo: useEffect (cleanup) called (should run on unmount/re-mount)');
    };
  }, []); // Empty dependency array means run once on mount and cleanup on unmount

  return (
    <div style={{ border: '1px dashed green', padding: '10px', margin: '10px', backgroundColor: '#e6ffe6' }}>
      <h3>Functional Component Demo</h3>
      <p>Check console for logs.</p>
    </div>
  );
}

// --- App Component ---
function App() {
  return (
    <React.StrictMode>
      <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '800px', margin: '20px auto', padding: '20px', border: '1px solid #eee', boxShadow: '2px 2px 5px rgba(0,0,0,0.1)' }}>
        <h1>React.StrictMode Demonstration</h1>
        <p>Open your browser's developer console to observe the logs and warnings from StrictMode.</p>
        <p>Notice how certain messages might appear twice, indicating checks for unexpected side effects.</p>
        <p>Also, look for warnings about deprecated lifecycle methods.</p>

        <StrictModeDemoComponent />
        <FunctionalStrictModeDemo />

        <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
          <h3>Observations Explanation:</h3>
          <ul>
            <li>The <b>Constructor</b> and <b>Render</b> methods (both class and functional component bodies) are logged twice. This is StrictMode's way of detecting potential side effects. If these methods were not pure (e.g., directly modifying the DOM or global state), running them twice could lead to bugs.</li>
            <li><code>UNSAFE_componentWillMount</code> (if uncommented) will trigger a warning. StrictMode helps identify legacy and problematic lifecycle methods.</li>
            <li><code>componentDidMount</code> and <code>useEffect</code> with an empty dependency array (`[]`) are logged only once. These are the correct places for side effects that should run only once after a component mounts, as React ensures they are not double-invoked.</li>
          </ul>
        </div>

      </div>
    </React.StrictMode>
  );
}

export default App;

