import React from 'react';
import { BrowserRouter, Routes, Route, Link, Outlet, useParams } from 'react-router-dom';
import ReactDOM from 'react-dom/client';

function HomePage() {
  return <h2>Welcome to the Blog!</h2>;
}

function AboutPage() {
  return <h2>About Us</h2>;
}

function PostsLayout() {
  const posts = [
    { id: '1', title: 'First Blog Post' },
    { id: '2', title: 'Second Blog Post' },
    { id: '3', title: 'Third Blog Post' },
  ];

  return (
    <div>
      <h2>Blog Posts</h2>
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            <Link to={post.id}>{post.title}</Link>
          </li>
        ))}
      </ul>
      <hr />
      {/* This is where the nested route content will be rendered */}
      <Outlet /> 
    </div>
  );
}

function PostDetails() {
  const { postId } = useParams();
  return (
    <div>
      <h3>Details for Post: {postId}</h3>
      <p>This is the full content for post #{postId}.</p>
    </div>
  );
}

function NotFoundPage() {
  return <h2>404 - Page Not Found</h2>;
}

function App() {
  return (
    <BrowserRouter>
      <nav style={{ padding: '10px 0', borderBottom: '1px solid #ccc' }}>
        <Link to="/" style={{ margin: '0 10px' }}>Home</Link> 
        <Link to="/about" style={{ margin: '0 10px' }}>About</Link> 
        <Link to="/posts" style={{ margin: '0 10px' }}>Posts</Link>
      </nav>
      <div style={{ padding: '20px' }}>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutPage />} />

          {/* Parent route for blog posts */}
          <Route path="/posts" element={<PostsLayout />}>
            {/* Nested route for individual post details */}
            <Route path=":postId" element={<PostDetails />} />
            {/* Optional: default content when only /posts is matched */}
            <Route index element={<h4>Select a post to read details.</h4>} />
          </Route>

          {/* 404 Not Found route */}
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);

