import React, { useState } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';

// Simulate an API call for login
const fakeLoginApi = async (username, password) => {
  return new Promise(resolve => {
    setTimeout(() => {
      if (username === 'user' && password === 'password') {
        resolve(true); // Simulate successful login
      } else {
        resolve(false); // Simulate failed login
      }
    }, 700);
  });
};

function LoginComponent() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Initialize useNavigate hook

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    const success = await fakeLoginApi(username, password);
    if (success) {
      setIsLoggedIn(true); // Update state to reflect login status
      // Programmatic redirect to dashboard and replace history entry
      navigate('/dashboard', { replace: true });
    } else {
      setError('Invalid username or password. Try 'user' / 'password'.');
    }
  };

  // Optional: Declarative redirect using <Navigate> (if login state handled externally/globally)
